<div class="l-basket" data-ng-controller="CartMiniController" data-ng-init="count=<?= (int)$count ?>">
    <a class="b-basket__link"
       title="{{getTitle()}}"
       href="<?= link_to('shop_cart') ?>"
       data-ng-cloak
       data-ng-class="{'b-basket__link_no':count==0}">
        <div class="b-basket " data-ng-class="{'b-basket_empty':count==0}">
            <span  data-ng-bind="count | ifEmpty:'<?= $count ?>' "></span>
            <img src="data:image/svg+xml;base64," alt="Корзина">
        </div>
    </a>
    <input class="js-cart-update" type="hidden" data-ng-click="init(false)">
</div>
